import {Component} from 'react';

class Counter extends Component{
    constructor(){
        super();
        this.state = 0;
    }

    increment(obj){
        //this.setState(({counter}) => ++counter);
        console.log(obj.state);
    }

    decrement(){
        if(this.state.counter > 0) this.setState(({counter}) => --counter);
    }

    render(){
        return(
            <p>
                <p>{this.state}</p>
                <button onClick={() => {this.increment(this)}} style={{backgroundColor: "powderblue", color: "white"}}>-</button>
                <p>{this.state}</p>
                <button onClick={() => {this.increment(this)}} style={{backgroundColor: "powderblue", color: "white"}}>+</button>
            </p>
        );
    }
}

export default Counter;
