import {Component} from 'react';

class Counter extends Component{
    constructor(){
        super();
        this.state = {counter: 0};
    }

    increment = () => {
        this.setState({...this.state, counter: this.state.counter+1});
    }

    decrement = () => {
        this.setState({...this.state, counter: this.state.counter > 0 ? this.state.counter-1 : 0});
    }

    render(){
        return(
            <table>
                <tbody>
                    <tr>
                        <td colSpan="2"><p>{this.state.counter === 0 ? "Zero" : this.state.counter}</p></td>
                        <td><button onClick={this.decrement} style={{backgroundColor: "powderblue", color: "white"}}>-</button></td>
                        <td colSpan="2"><p>{this.state.counter === 0 ? " " : this.state.counter}</p></td>
                        <td><button onClick={this.increment} style={{backgroundColor: "powderblue", color: "white"}}>+</button></td>
                    </tr>
                </tbody>
            </table>
        );
    }
}

export default Counter;
