import {Component} from 'react';

class Item extends Component{
    constructor(){
        super();
        this.state = {display: true, counter: 0};
    }


    increment = () => {
        this.setState({...this.state, counter: this.state.counter+1});
    }

    decrement = () => {
        this.setState({...this.state, counter: this.state.counter > 0 ? this.state.counter-1 : 0});
    }

    click = () => {
        this.setState({...this.state, display: !this.state.display});
    }

    render(){
        return (
            <table>
                <tbody>
                    <tr>
                        <td>{this.state.counter === 0 ? "Zero" : this.state.counter}</td>
                        <td><button onClick={this.decrement} style={{backgroundColor: "powderblue", color: "white"}}>-</button></td>
                        <td>{this.state.counter === 0 ? " " : this.state.counter}</td>
                        <td><button onClick={this.increment} style={{backgroundColor: "powderblue", color: "white"}}>+</button></td>
                        <td><button onClick={this.click} style={{backgroundColor: "red", color: "white"}}>Delete</button></td>
                    </tr>
                </tbody>
            </table>
        ); 
    }
}

export default Item;
