import React, { Component } from "react";

class Counter extends Component {
  state = {
    value: this.props.counter.value
  };

  styles = {
    fontSize: 10,
    fontWeight: 'bold',
    color: 'white'
  };

  /*constructor(){
    super();
    this.handleIncrement = this.handleIncrement.bind(this);
  }*/

  render() {
    return (
      <div>
        <span style={ this.styles } className={ `badge badge-${this.formatBadgeType()}` }> { this.formatValue() } </span>
        <button onClick={this.handleIncrement} className="btn btn-secondary btn-sm m-2"> Increment </button>
        <button className="btn btn-danger btn-sm" onClick={ () => this.props.onDelete(this.props.counter.id) }>Delete</button>
      </div>
    );
  }

  handleIncrement = () => {
    this.setState({ value: this.state.value + 1 });
  }

  formatValue(){
    const {value} = this.state;
    return value === 0 ? 'Zero' : value;
  }

  formatBadgeType(){
    const {value} = this.state;
    return value === 0 ? 'warning' : 'primary';
  }
}

export default Counter;
