import {Component} from 'react';

class Counter extends Component{
    constructor(){
        this.counter = 0;
    }

    increment(){
        this.setCounter((c) => c+1);
    }

    decrement(){
        if(this.state.counter > 0) this.setState(({counter}) => --counter);
    }

    render(){
        return(
            <p>
                <p>{this.state.counter}</p>
                <button onClick={this.decrement} style={{backgroundColor: "powderblue", color: "white"}}>-</button>
                <p>{this.state.counter}</p>
                <button onClick={this.increment} style={{backgroundColor: "powderblue", color: "white"}}>+</button>
            </p>
        );
    }
}

export default Counter;
