import React, { Component } from "react";

class Counter extends Component {
  state = {
    count:1
  };

  render() {
    return (
      <span>
        <span>{this.formatCount()}</span>
        <button>Increment</button>
      </span>
    );
  }

  formatCount(){
    const {count} = this.state;
    return count === 0 ? <h1>Zero</h1> : count;
  }
}

export default Counter;
