import {Component} from 'react';
import Counter from './Counter.js';
import Delete from './Delete.js';

class Item extends Component{
    constructor(){
        super();
        this.state = {display: true, ref: this};
    }

    clickAction(){
        this.setState({display: !this.state.display});
        console.log("hello");
    }

    html = () => {
        if(this.state.display){
            return (
                <table>
                    <tbody>
                        <tr>
                            <td><p>{this.state.counter === 0 ? "Zero" : this.state.counter}</p></td>
                            <td><button onClick={this.decrement} style={{backgroundColor: "powderblue", color: "white"}}>-</button></td>
                            <td><p>{this.state.counter === 0 ? " " : this.state.counter}</p></td>
                            <td><button onClick={this.increment} style={{backgroundColor: "powderblue", color: "white"}}>+</button></td>
                            <td><button style = {{backgroundColor: "red", color: "white"}}>Delete</button></td>
                        </tr>
                    </tbody>
                </table>
            );
        }
    }

    render(){
        return this.html(); 
    }
}

export default Item;
