import {Component} from 'react';

class Counter extends Component{
    constructor(){
        super();
        this.state = {counter: 0};
    }

    increment = () => {
        this.setState((s) => {s.counter += 1});
    }

    decrement = () => {
        this.setState((s) => {s.counter -= 1});
    }

    render(){
        return(
            <p>
                <p>{this.state}</p>
                <button onClick={this.decrement} style={{backgroundColor: "powderblue", color: "white"}}>-</button>
                <p>{this.state}</p>
                <button onClick={this.increment} style={{backgroundColor: "powderblue", color: "white"}}>+</button>
            </p>
        );
    }
}

export default Counter;
