import {Component} from 'react';

class Counter extends Component{
    constructor(){
        super();
        this.state = 0;
    }

    increment(self){
        self.setState((s) => {return {...s, counter: s.counter+1}});
    }

    decrement(self){
        if(self.state.counter > 0) self.setState(({counter}) => --counter);
    }

    render(){
        return(
            <p>
                <p>{this.state}</p>
                <button onClick={() => {this.decrement(this)}} style={{backgroundColor: "powderblue", color: "white"}}>-</button>
                <p>{this.state}</p>
                <button onClick={() => {this.increment(this)}} style={{backgroundColor: "powderblue", color: "white"}}>+</button>
            </p>
        );
    }
}

export default Counter;
